package com.juaracoding.Latihan.BootcampJuaraCoding.Looping;

public class OpTenary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean suka = true;
		boolean manis = false;
		
		String jawaban;
		String jawaban2;
		
		jawaban = suka ? "Iya" : "Enggak";
		jawaban2 = manis ? "Iya" : "Tidak"; 
		System.out.println(jawaban);
		System.out.println("Apakah Garam Manis = "+jawaban2);
	}

}
